import {useState} from 'react';

function Weather() {
    const [lat, setLat] = useState('');
    const [lon, setLon] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [loading, setLoading] = useState(false);

    function handleInputChange(event){
        const value = event.target.value;
        const [latitude, longitude] = value.split(',').map(coord => coord.trim());
        setLat(latitude);
        setLon(longitude);
    }

    const fetchWeather = () => {
        setLoading(true);
        
        // Get user's location
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const latitude = position.coords.latitude;
                    const longitude = position.coords.longitude;
                    
                    setLat(latitude);
                    setLon(longitude);
                    
                    // Fetch weather data
                    fetch(`http://localhost:8000/weather?lat=${latitude}&lon=${longitude}`)
                        .then((response) => response.json())
                        .then((data) => {
                            setWeatherData(data);
                            setLoading(false);
                        })
                        .catch((error) => {
                            console.error('Error fetching weather data:', error);
                            setLoading(false);
                        });
                },
                (error) => {
                    console.error('Error getting location:', error);
                    setLoading(false);
                    alert('Please allow location access to get weather data');
                }
            );
        } else {
            alert('Geolocation is not supported by this browser');
            setLoading(false);
        }
    };    return(
        <div className = "Background">
            <h1 className = "heading">Breeze</h1>
            <div className = "left-side">
                <input className = "input"
                    type = 'text'
                    placeholder = 'Enter Latitude & Longitude'
                    value = {`${lat}, ${lon}`}
                    onChange = {handleInputChange}
                />
                <button className = "button" onClick={fetchWeather}>
                    {loading ? 'Loading...' : 'Get Weather'}
                </button>
            </div>
        </div>
    )
}


export default Weather;
