import {useState} from 'react';

function Weather() {
    const [lat, setLat] = useState('');
    const [lon, setLon] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [loading, setLoading] = useState(false);

    function handleInputChange(event){
        const value = event.target.value;
        const [latitude, longitude] = value.split('&').map(coord => coord.trim());
        setLat(latitude);
        setLon(longitude);
    }    const fetchWeather = () => {
        setLoading(true);
        
        // Check if user has entered coordinates manually
        if (lat && lon) {
            // Use manually entered coordinates
            fetch(`http://localhost:8000/weather?lat=${lat}&lon=${lon}`)
                .then((response) => response.json())
                .then((data) => {
                    setWeatherData(data);
                    setLoading(false);
                })
                .catch((error) => {
                    console.error('Error fetching weather data:', error);
                    setLoading(false);
                });
        } else {
            setLoading(false);
            alert('Please enter both latitude and longitude.');
        }
    };

    return(
        <div className = "Background">
            <h1 className = "heading">Breeze</h1>
            <div className = "left-side">
                <input className = "input"
                    type = 'text'
                    placeholder = 'Enter Latitude & Longitude'
                    value = {`${lat}, ${lon}`}
                    onChange = {handleInputChange}
                />
                <button className = "button" onClick={fetchWeather}>
                    {loading ? 'Loading...' : 'Get Weather'}
                </button>
            </div>
        </div>
    )
}


export default Weather;
